class DataBean {
  List<CategoriesBean> categories;
  List<RestaurantsBean> restaurants;

  static DataBean fromMap(Map<String, dynamic> map) {
    if (map == null) return null;
    DataBean dataBean = DataBean();
    dataBean.categories = List()..addAll(
      (map['categories'] as List ?? []).map((o) => CategoriesBean.fromMap(o))
    );
    dataBean.restaurants = List()..addAll(
      (map['restaurants'] as List ?? []).map((o) => RestaurantsBean.fromMap(o))
    );
    return dataBean;
  }

  Map toJson() => {
    "categories": categories,
    "restaurants": restaurants,
  };
}

